import React from 'react';
import Chart from './components/Chart';
import Calendar from './components/Calendar';
import Menu from './components/Menu';
import { DateRangePicker, SingleDatePicker, DayPickerRangeController } from 'react-dates';
import 'react-dates/initialize';
import 'react-dates/lib/css/_datepicker.css';


class App extends React.Component {
  constructor(){
    super();
    this.state = {
      chartData: {},
      chartData2: {},
      chartD: 'insect_count',
      chartType:'Bar',
      location: null,
      startDate: null,
      endDate: null,
      focusedInput: null
    }
  }




  BarClick = () => {
    this.setState({chartType:'Bar'});
  }

  LineClick = () => {
    this.setState({chartType:'Line'});
  }

  PieClick = () => {
    this.setState({chartType:'Pie'});
  }

  location1 = () => {
    this.setState({
      chartData:{
          labels: ['05-06', '05-07', '05-08', '05-09', '05-10', '05-11', '05-12', '05-13', '05-14'],
          datasets: [
            {
              label:'Device_1',
              data:[
                0,
                5.75,
                11.5,
                17.25,
                23
              ],
              backgroundColor:[
                'rgba(63, 63, 191, 0.6)',
                'rgba(63, 63, 191, 0.6)',
                'rgba(63, 63, 191, 0.6)',
                'rgba(63, 63, 191, 0.6)',
                'rgba(63, 63, 191, 0.6)'
              ]
            },
            {
              label:'Device_2',
              data:[
                5,
                10,
                15,
                20,
                25
              ],
              backgroundColor:[
                'rgba(63, 191, 63, 0.6)',
                'rgba(63, 191, 63, 0.6)',
                'rgba(63, 191, 63, 0.6)',
                'rgba(63, 191, 63, 0.6)',
                'rgba(63, 191, 63, 0.6)'
              ]
            },
            {
              label:'Device_3',
              data:[
                7,
                9,
                23,
                2,
                18
              ],
              backgroundColor:[
                'rgba(10, 255, 255, 0.6)',
                'rgba(10, 255, 255, 0.6)',
                'rgba(10, 255, 255, 0.6)',
                'rgba(10, 255, 255, 0.6)',
                'rgba(10, 255, 255, 0.6)'
              ]
            }
          ]
      }

    });
  }

  location2 = () => {
    this.setState({location:'Riverside'});
  }

  location3 = () => {
    this.setState({location:'Irvine'});
  }

  render() {
    console.log(this.state.chartData);
    const falseFunc = ()=>false;
    return (
      <div>
        <p> Select a Chart's Data: </p>
        <button onClick={this.BarClick}>Bar</button>
        <button onClick={this.LineClick}>Line</button>
        <button onClick={this.PieClick}>Pie</button>


        <p> Insect Count (per location) </p>
        <button onClick={this.location1}>Cupertino</button>
        <button onClick={this.location2}>Riverside</button>
        <button onClick={this.location3}>Irvine</button>

        <Chart chartData={this.state.chartData} chartData2={this.state.chartData2} location={this.state.location} chartType={this.state.chartType} temp={this.state.startDate} />
      </div>
    );
  }
};

export default App;
