import React from 'react';
import Calendar from './components/Calendar';
import Chart from './components/Chart';

class App extends React.Component {
  constructor(){
    super();
    this.state = {
      chartData:{}
    };
  }

  componentWillMount(){
    this.getChartData();
  }

  //rerender as needed
  componentDidMount(){

  }

  getChartData(){
    //Ajax calls here
    this.setState({
      chartData:{

          labels: ['05-06', '05-07', '05-08', '05-09', '05-10', '05-11', '05-12', '05-13', '05-14'],
          datasets: [
            {
              label:'Population',
              data:[
                60000,
                50000,
                40000,
                30000,
                20000
              ],
              backgroundColor:[
                'rgba(255, 99, 132, 0.6)',
                'rgba(255, 99, 132, 0.6)',
                'rgba(255, 99, 132, 0.6)',
                'rgba(255, 99, 132, 0.6)',
                'rgba(255, 99, 132, 0.6)'
              ]
            }
          ]

      }
    });
  }


  render() {
    return (
      <div>
      <Calendar />
      <Chart chartData={this.state.chartData} location="California"/>
      </div>
    );
  }
};

export default App;
