import React, {component} from 'react';
import { DateRangePicker, SingleDatePicker, DayPickerRangeController } from 'react-dates';
import 'react-dates/initialize';
import 'react-dates/lib/css/_datepicker.css';
import Chart from './Chart';
import Calendar from './Calendar';

class Section4 extends React.Component {
  constructor(){
    super();
    this.state = {
      chartData: {} ,
      chartType:'Bar',
      device: 'Sensor_1',
      units: 'Section4',
      focusedInput: null
    }
  }

    componentWillMount() {
      this.getChartData();
    }

    getChartData () {
      //make ajax/axios call
        this.setState({

          chartData:{
              labels: ['09-06', '09-07', '09-08', '09-09', '09-10', '09-11', '09-12', '09-13'],
              datasets: [
                {
                  data:[225, 230, 235, 240, 248, 238, 233, 250],
                  backgroundColor: 'rgba(63, 63, 191, 0.6)'
                }
              ]
          }
        });

    }
    BarClick = () => {
      this.setState({chartType:'Bar'});
    }

    LineClick = () => {
      this.setState({chartType:'Line'});
    }

    PieClick = () => {
      this.setState({chartType:'Pie'});
    }

    Sensor1 = () => {
      this.setState({
        device:'Sensor_1',
        chartBool: false,
        chartData:{
            labels: ['09-06', '09-07', '09-08', '09-09', '09-10', '09-11', '09-12', '09-13'],
            datasets: [
              {
                data:[225, 230, 235, 240, 248, 238, 233, 250],
                backgroundColor:'rgba(63, 63, 191, 0.6)'
              }
            ]
        }

      });
    }

    Sensor2 = () => {
      this.setState({
        device:'Sensor_2',
        chartBool: false,
        chartData:{
            labels: ['05-06', '05-07', '05-08', '05-09', '05-10', '05-11', '05-12', '05-13'],
            datasets: [
              {
                data:[300, 330, 270, 280, 290, 310, 330, 320],
                backgroundColor: 'rgba(63, 63, 191, 0.6)'
              }
            ]
        }
      });
    }

    range = (start, end) => {
      if (start != null && end != null){
        this.setState({
          startDate: start.format('MMM-DD'),
          endDate: end.format('MMM-DD')
        })

      }

}

  render() {
    const falseFunc = ()=>false;
    return (
      <div>
        <div>
          <p> Select a Chart's format </p>
          <button onClick={this.BarClick}>Bar</button>
          <button onClick={this.LineClick}>Line</button>
          <button onClick={this.PieClick}>Pie</button>

          <p> Select a Device </p>
          <button onClick={this.Sensor1}>Sensor_1</button>
          <button onClick={this.Sensor2}>Sensor_2</button>

          <p> Pick a date </p>
          <Calendar range={this.range}/>

          <Chart chartData = {this.state.chartData} title={this.state.device} chartType={this.state.chartType} units={this.state.units}/>
        </div>
      </div>
    );
  }
};

export default Section4;
