import React, {Component} from 'react';
import {Bar,Line,Pie} from 'react-chartjs-2';

class Chart extends Component {
  constructor(props){
    super(props);
    this.state = {
      chartData:props.chartData,
      error: null
    }
  }

  static defaultProps = {
    chartData:{

        labels: ['05-06', '05-07', '05-08', '05-09', '05-10', '05-11', '05-12', '05-13', '05-14'],
        datasets: [
          {
            label:'Population',
            data:[
              0,
              5.75,
              11.5,
              17.25,
              23
            ],
            backgroundColor:[
              'rgba(63, 63, 191, 0.6)',
              'rgba(63, 63, 191, 0.6)',
              'rgba(63, 63, 191, 0.6)',
              'rgba(63, 63, 191, 0.6)',
              'rgba(63, 63, 191, 0.6)',
            ]
          },
          {
            label:'Second Population',
            data:[
              5,
              10,
              15,
              20,
              25
            ],
            backgroundColor:[
              'rgba(63, 191, 63, 0.6)',
              'rgba(63, 191, 63, 0.6)',
              'rgba(63, 191, 63, 0.6)',
              'rgba(63, 191, 63, 0.6)',
              'rgba(63, 191, 63, 0.6)',
            ]
          }
        ]

    },
    displayTitle:true,
    displayLegend:true,
    legendPosition:'right',
    location:'Location'
  }

  render(){
    if (this.props.chartType=="Bar"){
      return (
        <div className="chart">
          <Bar
            options={{
              title:{
                display:this.props.displayTitle,
                text:'Location is in: ' + this.props.location,
              },
              legend:{
                display:this.props.displayLegend,
                position:'right'
              }
            }}
          />
        }
        </div>
      );
  }

    else if (this.props.chartType=="Bar"){
      return (
        <div className="chart">
          <Line

            options={{
              title:{
                display:this.props.displayTitle,
                text:'Location is in: ' + this.props.location,
              },
              legend:{
                display:this.props.displayLegend,
                position:'right'
              }
            }}
          />
        }
        </div>
      );
  }
}
};

export default Chart;
