import React, {Component} from 'react';
import {Bar,Line,Pie} from 'react-chartjs-2';
import moment from 'moment';

class Chart extends Component {
  constructor(props){
    super(props);
    this.state = {
      chartData: props.chartData,
      error: null,
      labels: ['1', '2', '3'],
      datasets: [{}],
    }
  }

  static defaultProps = {
    displayTitle:true,
    displayLegend:true,
    legendPosition:'right',
    location:'Location'
  }



  render(){
    if (this.props.chartType=='Bar' && this.props.units=='Section3'){
      return (
        <div className="chart">
          <Bar
            data={this.props.chartData}
            options={{
              legend: {
                display: false
              },
              scales: {
                yAxes: [{
                  ticks: {
                    beginAtZero: true,
                    max: 400,
                    callback: function(value, index, values) {
                      return value + "° C";
                    }
                  }
                }]
              },
              maintainAspectRatio: false,
              title:{
                display:this.props.displayTitle,
                text: this.props.title,
              }
            }}
          />
        </div>
      );
    }

    else if  (this.props.chartType=='Line' && this.props.units=='Section3'){
      return (
        <div className="chart">
          <Line
            data={this.props.chartData}
            options={{
              legend: {
                display: false
              },
              scales: {
                yAxes: [{
                  ticks: {
                    beginAtZero: true,
                    max: 400,
                    callback: function(value, index, values) {
                      return value + "° C";
                    }
                  }
                }]
              },
              maintainAspectRatio: false,
              title:{
                display:this.props.displayTitle,
                text: this.props.title,
              }
            }}
          />
        </div>
      );
    }

    else if  (this.props.chartType=='Pie' && this.props.units=='Section3'){
      return (
        <div className="chart">
          <Pie
            data={this.props.chartData}
            options={{
              legend: {
                display: false
              },
              scales: {
                yAxes: [{
                  ticks: {
                    beginAtZero: true,
                    max: 400,
                    callback: function(value, index, values) {
                      return value + "° C";
                    }
                  }
                }]
              },
              maintainAspectRatio: false,
              title:{
                display:this.props.displayTitle,
                text: this.props.title,
              }
            }}
          />
        </div>
      );
    }

    else if (this.props.chartType=='Bar'){
      return (
        <div className="chart">
          <Bar
            data={this.props.chartData}
            options={{
              scales: {
                xAxes: [{
                  time:
                  {
                    unit: 'month',
                    format: 'MM',
                    parser: 'MM',
                    displayFormats: { month: 'MM' },
                    max: moment('2017-10-09 18:43:53').format('MM'),
                    min: moment('2017-00-02 18:45:53').format('MM')
                  }
                }],
                yAxes: [{
                  ticks: {
                    beginAtZero: true
                  }
                }]
              },
              maintainAspectRatio: false,
              title:{
                display:this.props.displayTitle,
                text: this.props.title,
              },
              legend:{
                display:this.props.displayLegend,
                position:'right'
              }
            }}
          />
        </div>
      );
  }

    else if (this.props.chartType=='Line'){
      return (
        <div className="chart">
          <Line
            data={this.props.chartData}
            options={{
              scales: {
                yAxes: [{
                  ticks: {
                    beginAtZero: true
                  }
                }]
              },
              maintainAspectRatio: false,
              title:{
                display:this.props.displayTitle,
                text: this.props.title,
              },
              legend:{
                display:this.props.displayLegend,
                position:'right'
              }
            }}
          />
        </div>
      );
  }

  else if (this.props.chartType=='Pie'){
    return (
      <div className="chart">
        <Pie
          data={this.props.chartData}
          options={{
            scales: {
              yAxes: [{
                ticks: {
                  beginAtZero: true
                }
              }]
            },
            maintainAspectRatio: false,
            title:{
              display:this.props.displayTitle,
              text: this.props.title,
            },
            legend:{
              display:this.props.displayLegend,
              position:'right'
            }
          }}
        />
      </div>
    );
}
}
};

export default Chart;
